import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const Header = styled.header`
  width: 100%;
  height: auto;
  .container {
    max-width: 1200px;
    width: 100%;
    height: auto;
    .input_box {
      display: flex;
      justify-content: center;
      align-content: center;
      flex-direction: column;
      > div {
        flex: 0 0 33.333%;
      }
    }
  }
`;

export default () => {
  const [boards, setBoards] = useState(null);
  const [formData, setFormData] = useState({
    writer: '',
    title: '',
    contents: '',
  });

  const handleSubmit = async (formData) => {
    const res = await axios.post('http://localhost:3000/boards', formData);
    console.log(res.data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    console.log(formData)
  };

  useEffect(() => {
    const getAllBoards = async () => {
      const res = await axios.get('http://localhost:3000/boards');

      console.log(res.data);
      setBoards(res.data);
    };
    getAllBoards();
  }, []);
  return (
    <Header>
      <div className="container">
        <div className="input_box">
          <div className="writer_box">
            <input type="text" placeholder="글쓴이" onchange={() => handleChange} />
          </div>
          <div className="title_box">
            <input type="text" placeholder="제목" onchange={handleChange} />
          </div>
          <div className="contents_box">
            <input type="text" placeholder="내용" onchange={handleChange} />
          </div>
          <button type="submit" onclick={handleSubmit} />
        </div>
        <div className="boards_box">
          {boards && boards.map((board, i) => <Item>{board.writer}</Item>)}
        </div>
      </div>
    </Header>
  );
};
