import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const Header = styled.header`
  width: 100%;
  height: auto;
  .container {
    max-width: 1200px;
    width: 100%;
    height: auto;
    .input_box {
      display: flex;
      justify-content: center;
      align-content: center;
      flex-direction: column;
      > div {
        flex: 0 0 33.333%;
      }
    }
  }
`;
const Item = styled.div``;
export default () => {
  const [boards, setBoards] = useState();
  const [render, setRender] = useState(false);
  const [update, setUpdate] = useState(false);
  const [formData, setFormData] = useState({
    writer: '',
    title: '',
    contents: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post('http://localhost:3000/boards', formData);
    setRender(true);
    console.log(res.data, formData);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    console.log(formData);
  };
  const deleteBtn = async (id) => {
    console.log(id);
    const res = await axios.delete(`http://localhost:3000/boards/${id}`);
    setRender(true);
  };
  const goToUpdate = (updateData) => {
    setUpdate(true);
  };
  useEffect(() => {
    const getAllBoards = async () => {
      const res = await axios.get('http://localhost:3000/boards');

      console.log(res.data);
      setBoards(res.data);
    };
    getAllBoards();
    setRender(false);
  }, [render]);
  useEffect(() => {
    if(update) {
      setFormData({
        ...updateData,
        title: updateData.title,
        contents: updateData.contents,
      });
    }
    setUpdate(false)
  },[update])
  return (
    <Header>
      <div className="container">
        <div className="input_box">
          <div className="writer_box">
            <input
              type="text"
              name="writer"
              placeholder="글쓴이"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <div className="title_box">
            <input
              type="text"
              name="title"
              placeholder="제목"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <div className="contents_box">
            <input
              type="text"
              name="contents"
              placeholder="내용"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <button type="submit" onClick={handleSubmit}>
            등록
          </button>
        </div>
        <div className="boards_box">
          {boards &&
            boards.length > 0 &&
            boards.map((board, i) => (
              <Item key={i}>
                {`글쓴이 : ${board.writer}, 제목 : ${board.title}, 내용 : ${board.contents}`}
                <button type="button" onClick={() => deleteBtn(board._id)}>
                  삭제
                </button>
                <button type="button" onClick={() => goToUpdate(board)}>
                  수정
                </button>
              </Item>
            ))}
        </div>
      </div>
    </Header>
  );
};
